    myFocusNode.dispose();
    super.dispose();
  }

  void onSubmitInputString(
    String inputString
  ){
    setState(() {
      _items.add(inputString);
    });

    myController.clear();
    myFocusNode.requestFocus();
  }

  void submitInputStringAndClear(){
    onSubmitInputString(myController.text);
  }

  @override
  Widget build(BuildContext context) {
    final ColorScheme colorScheme = Theme.of(context).colorScheme;
    final Color oddItemColor = colorScheme.primary.withOpacity(0.05);
    final Color evenItemColor = colorScheme.primary.withOpacity(0.15);

    return Column(
      children: [
        Expanded(
            child: ReorderableListView(
              padding: const EdgeInsets.symmetric(horizontal: 0),
              children: <Widget>[
                for (int index = 0; index < _items.length; index += 1)
                  ListTile(
                    onTap: (){ myFocusNode.unfocus(); },
                    key: Key('$index'),
                    tileColor: index.isOdd ? oddItemColor : evenItemColor,
                    title: Text(_items[index]),
                  )
              ],
              onReorder: (int oldIndex, int newIndex) {
                setState(() {
                  if (oldIndex < newIndex) {
                    newIndex -= 1;
                  }
                  final String item = _items.removeAt(oldIndex);
                  _items.insert(newIndex, item);
                });
              },
            )
        ),
        Row(
          children: <Widget>[
            Expanded(
              child: TextField(
                focusNode: myFocusNode,
                textInputAction: TextInputAction.send,
                onSubmitted: onSubmitInputString,
                controller: myController,
                decoration: const InputDecoration(
                  border: OutlineInputBorder(),
                  hintText: 'Enter something to do',
                ),
              )
            ),
            FloatingActionButton(
              // When the user presses the button, show an alert dialog containing
              // the text that the user has entered into the text field.
              onPressed: submitInputStringAndClear,
              tooltip: 'Submit it!',
              child: const Icon(Icons.add),
            ),
          ],
        )
      ],
    );
  }
}

