  const FunkyList({Key? key}) : super(key: key);
  @override
  createState() => _FunkyListState();
}

class _FunkyListState extends State<FunkyList> {
  late Timer _everyMillis;

  double _expandingHeight = 0;

  @override
  void initState() {
    super.initState();

    _everyMillis = Timer.periodic(const Duration(milliseconds: 30), (Timer t) {
      if (mounted) {
        setState(() {
          // increate the height of the first item in the ListView every 30ms
          _expandingHeight += 1.0;
        });
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scrollbar(
      isAlwaysShown: true,
      child: CustomScrollView(
        slivers: [
          SliverToBoxAdapter(
            child: Padding(
              padding: const EdgeInsets.all(16.0),
              child: SizedBox(
                // increate the height of the first item in the ListView every 30ms, otherwise 200
                height: _expandingHeight,
                width: 30,
                child: const DecoratedBox(
                  decoration: BoxDecoration(color: Colors.red),
                ),
              ),
            )
          ),
          SliverList(
            delegate: SliverChildBuilderDelegate(
              (context, index) {
                return const Padding(
                  padding: EdgeInsets.all(16.0),
                  child: SizedBox(
                    height: 200.0,
                    width: 30,
                    child: DecoratedBox(
                      decoration: BoxDecoration(color: Colors.red),
                    ),
                  ),
                );
              },
              childCount: 39,
